import React, { useState, useEffect } from 'react';
import TopNavBar from './TopNavBar';
import ArtworkSlider from './ArtworkSlider';
import CategoryOptions from './CategoryOptions';
import './HomePage.css';

const HomePage = () => {
  const [artworks, setArtworks] = useState([]);

  useEffect(() => {
    // Sample artwork data - replace with API call later
    const sampleArtworks = [
      { id: 1, title: 'Anime Character 1', image: '/images/sample1.jpg', category: 'colored' },
      { id: 2, title: 'Anime Character 2', image: '/images/sample2.jpg', category: 'inked' },
      { id: 3, title: 'Anime Character 3', image: '/images/sample3.jpg', category: 'colored' },
      { id: 4, title: 'Anime Character 4', image: '/images/sample4.jpg', category: 'inked' },
    ];
    setArtworks(sampleArtworks);
  }, []);

  return (
    <div className="home-page">
      <TopNavBar />

      <div className="welcome-section">
        <h1 className="welcome-title">Welcome to Anime Art Gallery</h1>
        <p className="welcome-subtitle">Discover Amazing Anime Artwork</p>
      </div>

      <ArtworkSlider artworks={artworks} />

      <CategoryOptions />
    </div>
  );
};

export default HomePage;
